// STEP 1: Environment Validation
console.log('üéØ STEP 1: Environment Validation');

const requiredEnvVars = [
  'SENDER_EMAIL',
  'SENDER_GMAIL_PASSWORD', 
  'CLAUDE_API_KEY',
  'TARGET_EMAIL'
];

const missingVars = [];
for (const envVar of requiredEnvVars) {
  if (!process.env[envVar]) {
    missingVars.push(envVar);
  }
}

if (missingVars.length > 0) {
  throw new Error(`Missing environment variables: ${missingVars.join(', ')}`);
}

return [{
  json: {
    step: 1,
    name: 'Environment Validation',
    status: 'SUCCESS',
    message: 'All environment variables validated',
    timestamp: new Date().toISOString(),
    required_vars: requiredEnvVars,
    execution_log: [{
      step: 'STEP_1',
      message: 'Environment validation completed successfully',
      status: 'SUCCESS',
      timestamp: new Date().toISOString()
    }]
  }
}];

// STEP 3: Job Data Processing & Enhancement
console.log('üéØ STEP 3: Job Data Processing');

const jobs = items.map((item, index) => {
  const email = item.json;
  const subject = email.subject || '';
  const body = email.snippet || email.bodyPlain || '';
  const sender = email.from?.value?.[0]?.address || '';

  // Enhanced company extraction
  function extractCompany(subject, body, sender) {
    const content = `${subject} ${body}`;

    // Swedish patterns for company extraction
    const patterns = [
      /([A-Z√Ö√Ñ√ñ][a-z√•√§√∂\s&]+?)\s+(?:s√∂ker|ker)\s+nu\s+fler\s+talanger/,
      /([A-Z√Ö√Ñ√ñ][a-z√•√§√∂\s&]+?)\s+is\s+actively\s+hiring/,
      /Join\s+([A-Z√Ö√Ñ√ñ][a-z√•√§√∂\s&]+?)\s+as/,
      /^([A-Z√Ö√Ñ√ñ][a-z√•√§√∂\s&]+?)\s*[-‚Äì]\s*[A-Z]/
    ];

    for (const pattern of patterns) {
      const match = content.match(pattern);
      if (match) {
        return match[1].trim().replace(/\s+/g, ' ');
      }
    }

    // Known Swedish companies
    const knownCompanies = {
      'volvo': 'Volvo Group',
      'ericsson': 'Ericsson', 
      'spotify': 'Spotify',
      'klarna': 'Klarna',
      'skf': 'SKF Group'
    };

    const contentLower = content.toLowerCase();
    for (const [keyword, fullName] of Object.entries(knownCompanies)) {
      if (contentLower.includes(keyword)) {
        return fullName;
      }
    }

    return 'Technology Company';
  }

  // Extract job title
  function extractTitle(subject, body) {
    if (subject.toLowerCase().includes('ker nu fler talanger till')) {
      const match = subject.match(/ker nu fler talanger till\s+(.+)/i);
      if (match) return match[1].trim();
    }

    const titlePatterns = [
      /(?:job|position|role)[:\s]+([^\n\r,]+)/i,
      /([^\n\r,]+)\s+(?:position|role|job)/i
    ];

    for (const pattern of titlePatterns) {
      const match = subject.match(pattern) || body.match(pattern);
      if (match) return match[1].trim();
    }

    return 'Software Developer';
  }

  // Extract application URL
  function extractUrl(body) {
    const urlPatterns = [
      /https:\/\/[^\s]+\.linkedin\.com\/jobs\/view\/\d+/,
      /https:\/\/[^\s]+\.indeed\.com\/viewjob\?jk=[a-zA-Z0-9]+/,
      /https:\/\/[^\s]+\/jobs?\/[^\s]+/,
      /https:\/\/[^\s]+\/careers?\/[^\s]+/
    ];

    for (const pattern of urlPatterns) {
      const match = body.match(pattern);
      if (match) return match[0];
    }

    return '';
  }

  const company = extractCompany(subject, body, sender);
  const title = extractTitle(subject, body);
  const url = extractUrl(body);

  return {
    id: `job_${index + 1}_${Date.now()}`,
    title: title,
    company: company,
    description: body.substring(0, 1000),
    url: url,
    location: 'Sweden',
    keywords: ['software', 'developer', 'fullstack', 'backend', 'frontend'],
    source: 'gmail',
    email_subject: subject,
    body: body,
    sender: sender,
    processed_at: new Date().toISOString(),
    processing_id: `job_${index + 1}_${new Date().toISOString().slice(0, 10)}`
  };
});

console.log(`‚úÖ STEP 3: Processed ${jobs.length} jobs`);

return jobs.map(job => ({ json: job }));

// STEP 4C: Combine Generated Documents
console.log('üéØ STEP 4C: Document Combination');

// Get the job data from the first input
const jobData = $input.first().json;

// Get CV content from Claude API response
const cvResponse = $input.all()[1]?.json;
const cvContent = cvResponse?.content?.[0]?.text || 'CV generation failed';

// Get Cover Letter content from Claude API response  
const clResponse = $input.all()[2]?.json;
const clContent = clResponse?.content?.[0]?.text || 'Cover letter generation failed';

// Create document package
const documentPackage = {
  job: jobData,
  cv_content: cvContent,
  cover_letter_content: clContent,
  generated_at: new Date().toISOString(),
  cv_length: cvContent.length,
  cl_length: clContent.length,
  status: 'documents_generated'
};

console.log(`‚úÖ STEP 4C: Documents generated for ${jobData.company}`);
console.log(`   CV: ${cvContent.length} characters`);
console.log(`   CL: ${clContent.length} characters`);

return [{ json: documentPackage }];

// STEP 6: Results Summary & Logging
console.log('üéØ STEP 6: Results Summary');

const executionData = $input.all();
const totalJobs = executionData.length;
const successfulEmails = executionData.filter(item => item.json.status !== 'error').length;
const failedEmails = totalJobs - successfulEmails;

const summary = {
  execution_id: `n8n_automation_${Date.now()}`,
  execution_time: new Date().toISOString(),
  platform: 'n8n',
  workflow_name: 'Master Job Hunting Automation',
  total_jobs_processed: totalJobs,
  successful_applications: successfulEmails,
  failed_applications: failedEmails,
  success_rate: `${((successfulEmails / Math.max(totalJobs, 1)) * 100).toFixed(1)}%`,
  processed_jobs: executionData.map(item => ({
    title: item.json.job?.title || 'Unknown',
    company: item.json.job?.company || 'Unknown',
    processing_id: item.json.job?.processing_id || 'Unknown',
    status: item.json.status || 'completed'
  })),
  execution_log: [
    { step: 'STEP_1', message: 'Environment validation completed', status: 'SUCCESS' },
    { step: 'STEP_2', message: `Gmail scan found ${totalJobs} jobs`, status: 'SUCCESS' },
    { step: 'STEP_3', message: `Job data processing completed`, status: 'SUCCESS' },
    { step: 'STEP_4', message: `Document generation completed`, status: 'SUCCESS' },
    { step: 'STEP_5', message: `Email delivery: ${successfulEmails} successful, ${failedEmails} failed`, status: 'SUCCESS' },
    { step: 'STEP_6', message: 'Summary generation completed', status: 'SUCCESS' }
  ]
};

console.log('üéâ n8n AUTOMATION SUMMARY:');
console.log(`üìä Jobs Processed: ${totalJobs}`);
console.log(`‚úÖ Successful: ${successfulEmails}`);
console.log(`‚ùå Failed: ${failedEmails}`);
console.log(`üìà Success Rate: ${summary.success_rate}`);

return [{ json: summary }];
